// script.js
document.getElementById('adicionarTarefa').addEventListener('click', function() {
    const tarefaInput = document.getElementById('tarefaInput');
    const tarefaTexto = tarefaInput.value.trim();

    if (tarefaTexto !== "") {
        // Adicionar tarefa ao container
        adicionarTarefa(tarefaTexto);

        // Limpar o campo de entrada
        tarefaInput.value = "";
    } else {
        alert("Por favor, insira uma tarefa.");
    }
});

function adicionarTarefa(tarefaTexto) {
    // Criar um elemento de tarefa
    const tarefaDiv = document.createElement('div');
    tarefaDiv.classList.add('tarefa');
    
    // Analisar a tarefa e sugerir algo inteligente
    const sugestao = sugerirTarefa(tarefaTexto);
    
    tarefaDiv.innerHTML = `<p>${tarefaTexto}</p><p><em>Sugestão: ${sugestao}</em></p>`;
    
    // Adicionar a tarefa no container
    document.getElementById('tarefasContainer').appendChild(tarefaDiv);
}

